 5	@Entity
 6	@Table(name = "app_users") // Changed from default 'user' to avoid SQL keyword conflict
 7	public class User {
 8	
 9	 @Id
 10	 @GeneratedValue(strategy = GenerationType.IDENTITY)
 11	 private Long id;
 12	
 13	 @Column(nullable = false) // Ensures name is required
 14	 private String username;
 15	
 16	 @Column(nullable = false, unique = true) // Ensures email is required and unique
 17	 private String email;
 18	
 19	 @Column(nullable = false) // Ensures password is required
 20	 private String password;
 21	
 22	 private String role = "USER"; // Default role
 23	
 24	 // Constructors
 25	 public User() {
 26	 }
 27	
 28	 public User(String username, String email, String password) {
 29	 this.username = username;
 30	 this.email = email;
 31	 this.password = password;
 32	 }
 33	
