 3	import com.fooddelivery.util.JwtUtil;
 4	import com.fooddelivery.service.UserDetailsServiceImpl;
 5	
 6	import jakarta.servlet.FilterChain;
 7	import jakarta.servlet.ServletException;
 8	import jakarta.servlet.http.HttpServletRequest;
 9	import jakarta.servlet.http.HttpServletResponse;
 10	
 11	import org.springframework.beans.factory.annotation.Autowired;
 12	import org.springframework.stereotype.Component;
...
 30	
 31	 @Override
 32	 protected void doFilterInternal(HttpServletRequest request,
 33	 HttpServletResponse response,
 34	 FilterChain filterChain)
 35	 throws ServletException, IOException {
 36	
 37	 String path = request.getServletPath();
 38	
 39	 // Skip JWT check for auth and registration endpoints
 40	 if (path.startsWith("/auth/") || path.equals("/users/register")) {
 41	 filterChain.doFilter(request, response);
 42	 return;
 43	 }
 44	
 45	 final String authHeader = request.getHeader("Authorization");
 46	 String username = null;
 47	 String jwt = null;
 48	
 49	 if (authHeader != null && authHeader.startsWith("Bearer ")) {
 50	 jwt = authHeader.substring(7);
 51	 username = jwtUtil.extractUsername(jwt);
 52	 }
 53	
 54	 if (username != null && SecurityContextHolder.getContext().getAuthentication() == null) {
 55	 UserDetails userDetails = userDetailsService.loadUserByUsername(username);
 56	 // Fix: Pass username string instead of UserDetails object
 57	 if (jwtUtil.validateToken(jwt, userDetails.getUsername())) {
 58	 UsernamePasswordAuthenticationToken authToken =
 59	 new UsernamePasswordAuthenticationToken(
 60	 userDetails,
 61	 null,
 62	 userDetails.getAuthorities());
 63	 authToken.setDetails(new WebAuthenticationDetailsSource().buildDetails(request));
 64	 SecurityContextHolder.getContext().setAuthentication(authToken);
 65	 }
 66	 }
 67	
 68	 filterChain.doFilter(request, response);
 69	 }
 70	}...
Path: src/main/java/com/fooddelivery/util/JwtUtil.java
 1	package com.fooddelivery.util;
 2	
 3	import io.jsonwebtoken.Claims;
 4	import io.jsonwebtoken.Jwts;
 5	import io.jsonwebtoken.SignatureAlgorithm;
 6	import io.jsonwebtoken.security.Keys;
 7	import org.springframework.stereotype.Component;
 8	
 9	import java.security.Key;
 10	import java.util.Date;
 11	import java.util.function.Function;
 12	
 13	@Component
 14	public class JwtUtil {
 15	
 16	 private final String SECRET_KEY = "mySecretKey12345678901234567890123456789012345678901234567890";
 17	 private final Key key = Keys.hmacShaKeyFor(SECRET_KEY.getBytes());
 18	
 19	 public String generateToken(String username) {
 20	 return Jwts.builder()
 21	 .setSubject(username)
 22	 .setIssuedAt(new Date())
 23	 .setExpiration(new Date(System.currentTimeMillis() + 1000 * 60 * 60 * 10)) // 10 hours
 24	 .signWith(key, SignatureAlgorithm.HS256)
 25	 .compact();
 26	 }
 27	
 28	 public String extractUsername(String token) {
 29	 return extractClaim(token, Claims::getSubject);
 30	 }
 31	
 32	 public Date extractExpiration(String token) {
 33	 return extractClaim(token, Claims::getExpiration);
 34	 }
 35	
 36	 public <T> T extractClaim(String token, Function<Claims, T> claimsResolver) {
 37	 final Claims claims = extractAllClaims(token);
 38	 return claimsResolver.apply(claims);
 39	 }
 40	
 41	 private Claims extractAllClaims(String token) {
 42	 return Jwts.parserBuilder().setSigningKey(key).build().parseClaimsJws(token).getBody();
 43	 }
 44	
 45	 public Boolean isTokenExpired(String token) {
 46	 return extractExpiration(token).before(new Date());
 47	 }
 48	
 49	 public Boolean validateToken(String token, String username) {
 50	 final String extractedUsername = extractUsername(token);
 51	 return (extractedUsername.equals(username) && !isTokenExpired(token));
 52	 }
 53	}...
