 1	package com.fooddelivery.service;
 2	
 3	import com.fooddelivery.FoodItemDTO;
 4	import com.fooddelivery.model.FoodItem;
 5	import com.fooddelivery.repository.FoodItemRepository;
 6	import org.springframework.beans.factory.annotation.Autowired;
 7	import org.springframework.stereotype.Service;
 8	
 9	import java.util.List;
 10	import java.util.stream.Collectors;
 11	
 12	@Service
 13	public class FoodItemService {
 14	
 15	 @Autowired
 16	 private FoodItemRepository foodItemRepository;
 17	
 18	 public List<FoodItemDTO> getAllFoodItems() {
 19	 return foodItemRepository.findAll().stream()
 20	 .map(this::convertToDTO)
 21	 .collect(Collectors.toList());
 22	 }
 23	
 24	 public FoodItemDTO addFoodItem(FoodItemDTO dto) {
 25	 FoodItem item = new FoodItem(
 26	 dto.getFoodItemName(),
 27	 dto.getPrice(),
 28	 dto.getRestaurantId()
 29	
 30	 );
 31	 FoodItem saved = foodItemRepository.save(item);
 32	 return convertToDTO(saved);
 33	 }
 34	
 35	 private FoodItemDTO convertToDTO(FoodItem foodItem) {
 36	 return new FoodItemDTO(
 37	 foodItem.getId(), foodItem.getName(), foodItem.getPrice(), foodItem.getRestaurantId()
 38	 );
 39	
 40	 }
 41	}
