Path: src/main/java/com/fooddelivery/service/ItemService.java
 1	package com.fooddelivery.service;
 2	
 3	import com.fooddelivery.dto.ItemRequestDto;
 4	import com.fooddelivery.dto.ItemResponseDto;
 5	import com.fooddelivery.exception.ResourceNotFoundException;
 6	import com.fooddelivery.model.Item;
 7	import com.fooddelivery.repository.ItemRepository;
 8	import lombok.RequiredArgsConstructor;
 9	import org.springframework.stereotype.Service;
 10	import org.springframework.transaction.annotation.Transactional;
 11	
 12	import java.util.List;
 13	import java.util.stream.Collectors;
 14	
 15	@Service
 16	@RequiredArgsConstructor
 17	public class ItemService {
 18	
 19	 private final ItemRepository itemRepository;
 20	
 21	 public ItemResponseDto createItem(ItemRequestDto request) {
 22	 Item item = Item.builder()
 23	 .name(request.getName())
 24	 .description(request.getDescription())
 25	 .price(request.getPrice())
 26	 .available(request.getAvailable() != null ? request.getAvailable() : true)
 27	 .build();
 28	
 29	 Item saved = itemRepository.save(item);
 30	 return mapToResponseDto(saved);
 31	 }
 32	
 33	 public List<ItemResponseDto> getAllItems() {
 34	 return itemRepository.findAll()
 35	 .stream()
 36	 .map(this::mapToResponseDto)
 37	 .collect(Collectors.toList());
 38	 }
 39	
 40	 public ItemResponseDto getItemById(Long id) {
 41	 Item item = itemRepository.findById(id)
 42	 .orElseThrow(() -> new ResourceNotFoundException("Item not found with id: " + id));
 43	 return mapToResponseDto(item);
 44	 }
 45	
 46	 @Transactional
 47	 public ItemResponseDto updateItem(Long id, ItemRequestDto request) {
 48	 Item item = itemRepository.findById(id)
 49	 .orElseThrow(() -> new ResourceNotFoundException("Item not found with id: " + id));
 50	
 51	 item.setName(request.getName());
 52	 item.setDescription(request.getDescription());
 53	 item.setPrice(request.getPrice());
 54	 item.setAvailable(request.getAvailable() != null ? request.getAvailable() : true);
 55	
 56	 Item saved = itemRepository.save(item);
 57	 return mapToResponseDto(saved);
 58	 }
 59	
 60	 @Transactional
 61	 public void deleteItem(Long id) {
 62	 if (!itemRepository.existsById(id)) {
 63	 throw new ResourceNotFoundException("Item not found with id: " + id);
 64	 }
 65	 itemRepository.deleteById(id);
 66	 }
 67	
 68	 private ItemResponseDto mapToResponseDto(Item item) {
 69	 return ItemResponseDto.builder()
 70	 .id(item.getId())
 71	 .name(item.getName())
 72	 .description(item.getDescription())
 73	 .price(item.getPrice())
 74	 .available(item.getAvailable())
 75	 .build();
 76	 }
 77	}...
