 1	package com.fooddelivery.service;
 2	
 3	import com.fooddelivery.model.User;
 4	import com.fooddelivery.repository.UserRepository;
 5	import org.springframework.beans.factory.annotation.Autowired;
 6	import org.springframework.security.crypto.password.PasswordEncoder;
 7	import org.springframework.stereotype.Service;
 8	
 9	@Service
 10	public class UserService {
 11	
 12	 @Autowired
 13	 private UserRepository userRepository;
 14	
 15	 @Autowired
 16	 private PasswordEncoder passwordEncoder;
 17	
 18	 public User register(User user) {
 19	 if (userRepository.findByUsername(user.getUsername()).isPresent()) {
 20	 throw new RuntimeException("Username already exists");
 21	 }
 22	 user.setPassword(passwordEncoder.encode(user.getPassword()));
 23	 return userRepository.save(user);
 24	 }
 25	
 26	 public User login(String name, String rawPassword) {
 27	 User user = userRepository.findByUsername(name)
 28	 .orElseThrow(() -> new RuntimeException("User not found"));
 29	
 30	 if (passwordEncoder.matches(rawPassword, user.getPassword())) {
 31	 return user;
 32	 } else {
 33	 throw new RuntimeException("Invalid credentials");
 34	 }
 35	 }
 36	
 37	}
