 28	 @Bean
 29	 public SecurityFilterChain securityFilterChain(HttpSecurity http) throws Exception {
 30	 http
 31	 .cors(cors -> cors.configurationSource(corsConfigurationSource()))
 32	 .csrf(csrf -> csrf.disable())
 33	 .authorizeHttpRequests(auth -> auth
 34	 // Public endpoints - anyone can access
 35	 .requestMatchers("/auth/**", "/users/register").permitAll()
 36	
 37	 // Items endpoints - GET for all authenticated users, CUD only for ADMIN
 38	 .requestMatchers(HttpMethod.GET, "/api/items/**").hasAnyRole("USER", "ADMIN")
 39	 .requestMatchers(HttpMethod.POST, "/api/items/**").hasRole("ADMIN")
 40	 .requestMatchers(HttpMethod.PUT, "/api/items/**").hasRole("ADMIN")
 41	 .requestMatchers(HttpMethod.DELETE, "/api/items/**").hasRole("ADMIN")
 42	
 43	 // All other requests require authentication
 44	 .anyRequest().authenticated()
 45	 )
 46	 .sessionManagement(session -> session.sessionCreationPolicy(SessionCreationPolicy.STATELESS))
 47	 .addFilterBefore(jwtFilter, UsernamePasswordAuthenticationFilter.class);
 48	
 49	 return http.build();
 50	 }
 51	
 52	 @Bean
 53	 public AuthenticationManager authenticationManager(AuthenticationConfiguration config) throws Exception {
 54	 return config.getAuthenticationManager();
 55	 }
 56	
 57	 @Bean
 58	 public PasswordEncoder passwordEncoder() {
 59	 return new BCryptPasswordEncoder();
 60	 }
 61	
 62	 @Bean
 63	 public CorsConfigurationSource corsConfigurationSource() {
 64	 CorsConfiguration config = new CorsConfiguration();
 65	 config.setAllowedOrigins(List.of("*"));
 66	 config.setAllowedMethods(List.of("GET", "POST", "PUT", "DELETE", "OPTIONS"));
 67	 config.setAllowedHeaders(List.of("*"));
 68	 config.setAllowCredentials(true);
 69	
 70	 UrlBasedCorsConfigurationSource source = new UrlBasedCorsConfigurationSource();
 71	 source.registerCorsConfiguration("/**", config);
 72	 return source;
 73	 }
