Path: src/main/java/com/fooddelivery/exception/GlobalExceptionHandler.java
 1	package com.fooddelivery.exception;
 2	
 3	import org.springframework.http.ResponseEntity;
 4	import org.springframework.web.bind.MethodArgumentNotValidException;
 5	import org.springframework.security.access.AccessDeniedException;
 6	import org.springframework.web.bind.annotation.*;
 7	
 8	import java.util.HashMap;
 9	import java.util.Map;
 10	
 11	@RestControllerAdvice
 12	public class GlobalExceptionHandler {
 13	
 14	 @ExceptionHandler(ResourceNotFoundException.class)
 15	 public ResponseEntity<Map<String, String>> handleNotFound(ResourceNotFoundException ex) {
 16	 return ResponseEntity.status(404)
 17	 .body(Map.of("error", ex.getMessage()));
 18	 }
 19	
 20	 @ExceptionHandler(MethodArgumentNotValidException.class)
 21	 public ResponseEntity<Map<String, String>> handleValidation(MethodArgumentNotValidException ex) {
 22	 Map<String, String> errors = new HashMap<>();
 23	 ex.getBindingResult().getFieldErrors()
 24	 .forEach(fe -> errors.put(fe.getField(), fe.getDefaultMessage()));
 25	 return ResponseEntity.badRequest().body(errors);
 26	 }
 27	
 28	 @ExceptionHandler(AccessDeniedException.class)
 29	 public ResponseEntity<Map<String, String>> handleDenied(AccessDeniedException ex) {
 30	 return ResponseEntity.status(403)
 31	 .body(Map.of("error", "Forbidden"));
 32	 }
 33	
 34	 @ExceptionHandler(Exception.class)
 35	 public ResponseEntity<Map<String, String>> fallback(Exception ex) {
 36	 return ResponseEntity.status(500)
 37	 .body(Map.of("error", "Internal server error"));
 38	 }
 39	}
