 12	
 13	import java.util.*;
 14	
 15	@RestController
 16	@RequestMapping("/users")
 17	public class UserController {
 18	
 19	 @Autowired
 20	 private UserService userService;
 21	
 22	 @Autowired
 23	 private JwtUtil jwtUtil;
 24	 @PostMapping("/register")
 25	 public ResponseEntity<String> register(@RequestBody UserDTO userDTO) {
 26	 try {
 27	 User user = new User();
 28	 user.setUsername(userDTO.getUsername()); // Make sure this matches your entity
 29	 user.setEmail(userDTO.getEmail());
 30	 user.setPassword(userDTO.getPassword()); // Will be encoded in service
 31	 user.setRole("USER");
 32	
 33	 userService.register(user);
 34	 return ResponseEntity.ok("User registered successfully!");
 35	 } catch (Exception e) {
