 42	 FoodItem foodItem = order.getFoodItem();
 43	 if (foodItem == null) {
 44	 throw new IllegalStateException("FoodItem not found for order: " + order.getId());
 45	 }
 46	 return new OrderDTO(
 47	 order.getId(),
 48	 order.getCustomerName(),
 49	 foodItem.getName(),
 50	 foodItem.getPrice(),
 51	 order.getQuantity()
 52	 );
 53	 })
 54	 .collect(Collectors.toList());
