 2	
 3	import com.fooddelivery.dto.OrderDTO;
 4	import com.fooddelivery.model.FoodItem;
 5	import com.fooddelivery.model.Order;
 6	import com.fooddelivery.repository.FoodItemRepository;
 7	import com.fooddelivery.repository.OrderRepository;
 8	import org.springframework.beans.factory.annotation.Autowired;
 9	import org.springframework.stereotype.Service;
 10	
 11	import java.util.List;
 12	import java.util.stream.Collectors;
 13	
 14	@Service
 15	public class OrderService {
 16	
 17	 @Autowired
 18	 private OrderRepository orderRepository;
 19	
 20	 @Autowired
 21	 private FoodItemRepository foodItemRepository;
 22	
 23	 public OrderDTO placeOrder(String customerName, int foodItemId, int quantity) {
 24	 FoodItem foodItem = foodItemRepository.findById(foodItemId)
 25	 .orElseThrow(() -> new RuntimeException("Food item not found with id: " + foodItemId));
 26	
 27	 Order order = new Order(customerName, foodItem, quantity);
 28	 Order savedOrder = orderRepository.save(order);
 29	
 30	 return new OrderDTO(
 31	 savedOrder.getId(),
 32	 savedOrder.getCustomerName(),
 33	 foodItem.getName(),
 34	 foodItem.getPrice(),
 35	 savedOrder.getQuantity()
 36	 );
 37	 }
 38	
 39	 public List<OrderDTO> getAllOrders() {
 40	 return orderRepository.findAll().stream()
 41	 .map(order -> {
 42	 FoodItem foodItem = order.getFoodItem();
 43	 if (foodItem == null) {
 44	 throw new IllegalStateException("FoodItem not found for order: " + order.getId());
 45	 }
 46	 return new OrderDTO(
 47	 order.getId(),
 48	 order.getCustomerName(),
 49	 foodItem.getName(),
 50	 foodItem.getPrice(),
 51	 order.getQuantity()
 52	 );
 53	 })
 54	 .collect(Collectors.toList());
 55	 }
 56	}...
