 33	 this.jwtUtil = jwtUtil;
 34	 this.userDetailsService = userDetailsService;
 35	 }
 36	
 37	 @PostMapping("/login")
 38	 public ResponseEntity<?> login(@RequestBody LoginRequest request) {
 39	 try {
 40	 // 1. Authenticate credentials
 41	 Authentication authentication = authenticationManager.authenticate(
 42	 new UsernamePasswordAuthenticationToken(
 43	 request.getUsername(),
 44	 request.getPassword()
 45	 )
 46	 );
 47	
 48	 // 2. Generate JWT - Fix: pass username string, not UserDetails object
 49	 String token = jwtUtil.generateToken(request.getUsername());
 50	
 51	 // 3. Return response
 52	 return ResponseEntity.ok(new AuthResponse(token));
 53	
 54	 } catch (BadCredentialsException e) {
 55	 return ResponseEntity.status(HttpStatus.UNAUTHORIZED).body("Invalid username/password");
 56	 }
 57	 }
